mapper.select:
select file_id, board_id, file_name, file_size, file_content_type, file_url, reg_dt
from board_file
where board_id = #{boardId}

mapper.update:
update board
set title = #{title}
	, content = #{content}
where board_id = #{boardId}

mapper.select:
select count(*)
from board_user_read
where board_id = #{boardId}
	  and user_seq = #{userSeq}

mapper.update:
update board
set read_count = read_count + 1
where board_id = #{boardId}
